const SlashCommand = Extension.create({
  name: 'slashCommand',
  addOptions() {
    const suggestion: Omit<SuggestionOptions, 'editor'> = {
      char: '/',
      command: ({ editor, range, props }) => props.command({ editor, range }),
      render: () => {
        return {
          onStart: ({ editor }) => {
            editor.storage.slashCommand.open = true;
          },
          onExit: ({ editor }) => {
            editor.storage.slashCommand.open = false;
          },
        };
      },
    };

    return {
      suggestion,
    };
  },
  addStorage() {
    return {
      open: false,
      range: null,
    };
  },
  addProseMirrorPlugins() {
    return [
      Suggestion({
        editor: this.editor,
        ...this.options.suggestion,
      }),
    ];
  },
});

export interface SuggestionItem {
  title: string;
  description: string;
  icon: ReactNode;
  searchTerms?: string[];
  command?: (props: { editor: Editor; range: Range }) => void;
}

// Create a lowlight instance with all languages loaded
const lowlight = createLowlight(all);

export type EditorProviderProps = TiptapEditorProviderProps & {
  className?: string;
  limit?: number;
  placeholder?: string;
};

// StarterKit contains the following:
// - Blockquote
// - BulletList
// - CodeBlock (disabled for lowlight version)
// - Document
// - HardBreak
// - Heading
// - HorizontalRule
// - ListItem
// - OrderedList
// - Paragraph
// - Text
// - Marks
// - Bold
// - Code
// - Italic
// - Strike
// - Extensions
// - Dropcursor
// - Gapcursor
// - History

export const EditorProvider = ({
  className,
  extensions,
  limit,
  placeholder,
  ...props
}: EditorProviderProps) => {
  const defaultExtensions = [
    StarterKit.configure({
      codeBlock: false,
      bulletList: {
        HTMLAttributes: {
          class: cn('-mt-2 list-outside list-disc leading-3'),
        },
      },
      orderedList: {
        HTMLAttributes: {
          class: cn('-mt-2 list-outside list-decimal leading-3'),
        },
      },
      listItem: {
        HTMLAttributes: {
          class: cn('-mb-2 leading-normal'),
        },
      },
      blockquote: {
        HTMLAttributes: {
          class: cn('border-primary border-l-4'),
        },
      },
      code: {
        HTMLAttributes: {
          class: cn('rounded-md bg-muted px-1.5 py-1 font-medium font-mono'),
          spellcheck: 'false',
        },
      },
      horizontalRule: {
        HTMLAttributes: {
          class: cn('mt-4 mb-6 border-muted-foreground border-t'),
        },
      },
      dropcursor: {
        color: 'var(--border)',
        width: 4,
      },
    }),
    Color,
    Typography,
    Placeholder.configure({
      placeholder,
      emptyEditorClass:
        'before:text-muted-foreground before:content-[attr(data-placeholder)] before:float-left before:h-0 before:pointer-events-none',
    }),
    CharacterCount.configure({
      limit,
    }),
    CodeBlockLowlight.configure({
      lowlight,
    }),
    Superscript,
    Subscript,
    SlashCommand,
  ];

  return (
    <div className={cn(className, '[&>.ProseMirror-focused]:outline-none')}>
      <TiptapEditorProvider
        extensions={[...defaultExtensions, ...(extensions ?? [])]}
        {...props}
      />
    </div>
  );
};

export const useCharacterCount = () => {
  const { editor } = useCurrentEditor();

  if (!editor) {
    return 0;
  }

  return {
    characters: editor.storage.characterCount.characters(),
    words: editor.storage.characterCount.words(),
  };
};

