type EditorSlashMenuButtonProps = {
  title: string;
  description: string;
  searchTerms: string[];
  icon: LucideIcon;
  command: (props: { editor: Editor; range: Range }) => void;
};

const EditorSlashMenuButton = ({
  title,
  description,
  searchTerms,
  icon: Icon,
  command,
}: EditorSlashMenuButtonProps) => {
  const { editor } = useCurrentEditor();

  if (!editor) {
    return null;
  }

  const { from, to } = editor.state.selection;
  const range: Range = { from, to };

  return (
    <CommandItem onSelect={() => command({ editor, range })}>
      <div className="flex size-10 items-center justify-center rounded-md border border-muted bg-background">
        <Icon size={16} />
      </div>
      <div>
        <p className="font-medium">{title}</p>
        <p className="text-muted-foreground text-xs">{description}</p>
      </div>
    </CommandItem>
  );
};

export const EditorSlashTextButton = () => (
  <EditorSlashMenuButton
    title="Text"
    description="Just start typing with plain text."
    searchTerms={['p', 'paragraph']}
    icon={TextIcon}
    command={({ editor, range }) => {
      editor
        .chain()
        .focus()
        .deleteRange(range)
        .toggleNode('paragraph', 'paragraph')
        .run();
    }}
  />
);
